import React, { useState } from 'react'
import Comm from 'C:/Users/PC/Desktop/FullStack/part2-2/src/services/Comm.js'

const PersonForm = ({persons, setPersons}) => {
    const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState(0)

  const handleNoteChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const addName = (event) => {
    event.preventDefault();
    const nameObject = {
      name: newName,
      number: newNumber
    }
    const i = persons.filter(person => person.name === newName)
    if(i.length===0){  
      Comm.create(nameObject).then(
          returnedPerson => {
            setPersons(persons.concat(returnedPerson))
            setNewName('')
            setNewNumber(0)
          }
      )
    }else{
      if(window.confirm(newName+' is already on the list, replace older number with new one?')){
          const old = persons.find(element => element.name===newName)
          const id = old.id
          Comm.update(id, newObject).then(
            returnedPerson => {
              setPersons(persons.map(element => element.id===id? newObject : element))
              setNewName('')
              setNewNumber(0)
            }
          )
      }
    }
    
  }

    return(
        <>
            <form onSubmit={addName}>
                <div>
                    name: <input value={newName} onChange={handleNoteChange}/>
                </div>
                <div>
                    number: <input value={newNumber} onChange={handleNumberChange}/>
                </div>
                <div>
                    <button type="submit">add</button>
                </div>
            </form>
        </>
    )

}

export default PersonForm
