import React, { useState } from 'react'

const App = () => {
  const [ persons, setPersons ] = useState([{ name: 'Arto Hellas', number:'912345678'}]) 
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState(0)
  const [filter, setFilter] = useState('')
  const [filterArr,setFilterArr] = useState([])

  const handleNoteChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleFilterChange = (event) =>{
    setFilter(event.target.value)
  }

  const addFilter = (event) =>{
    event.preventDefault();
    const x = persons.filter(person => person.name.includes(filter))
    setFilterArr(x)
  }

  const addName = (event) => {
    event.preventDefault();
    const nameObject = {
      name: newName,
      number: newNumber
    }
    const i = persons.filter(person => person.name === newName)
    console.log("here",i)
    if(i.length===0){  
      setPersons(persons.concat(nameObject))
      setNewName('')
      setNewNumber(0)
    }else{
      window.alert(newName+' is already in the PhoneList');
    }
    
  }


  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addFilter}>
        <div>
          filter shown with <input value={filter} onChange={handleFilterChange}/>
        </div>
        <div>
        <button type="submit">filter</button>
        </div>

      
      </form>
      <ul>
        {filterArr.map(({name,number}) => <li key={'filter'+name}>{name} {number}</li>)}
      </ul>
      <h2>Add a new</h2>
      <form onSubmit={addName}>
        <div>
          name: <input value={newName} onChange={handleNoteChange}/>
        </div>
        <div>
          number: <input value={newNumber} onChange={handleNumberChange}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <ul>
        {persons.map(({name, number})=><li key={name}>{name} {number}</li>)}
      </ul>
    </div>
  )
}

export default App
